void terminador_cad (char *cad) {
	do {
		if (*cad == '\n') {
			*cad = '\0';
		}
		
		cad++;
	}while (*cad != '\0');
}

void confirmacion (char *resp) {
	fflush (stdin);
	
	while (*resp != 'S' && *resp != 's' && *resp != 'N' && *resp != 'n') {
		printf ("Seleccione una opcion valida: ");
		scanf ("%c", resp);
		fflush (stdin);
	}
}

int input_int () {
	int verif_int, num;
	
	fflush(stdin);
	
	do{
		verif_int = scanf ("%d", &num);
		
		fflush(stdin);
		
		if (verif_int != 1) {
			printf ("Introduzca un numero entero: ");
		}
	}while (verif_int != 1);
	
	return num;
}